  const plusOne = () => {
    setCounter(counter + 1);
  };

  const minusOne = () => {
    setCounter(counter - 1);
  };

    return (
      <CounterContext.Provider value={{ minusOne , plusOne}}>
        {children}
      </CounterContext.Provider>
    );
};
